#!/bin/bash
main_menu(){ clear; echo "Welcome to the File Permissions Manager"; echo "---------------------------------------"; echo "1. Navigate Directories"; echo "2. Change File Permissions"; echo "3. Reset"; echo "4. Exit"; echo "---------------------------------------"; read -p "Please enter your choice: " c; case $c in 1) navigate ;; 2) change_permissions ;; 3) reset ;; 4) exit 0 ;; *) sleep 1 && main_menu ;; esac; }
navigate(){ while true; do clear; echo "Current Directory: $(pwd)"; echo "---------------------------------------"; dirs=(*/); files=(*); echo "0. .. (Go up)"; for i in "${!dirs[@]}"; do echo "$((i+1)). ${dirs[i]}"; done; echo "---------------------------------------"; echo "[Enter number to enter folder | q to stop]"; read -p "Choice: " nav; [ "$nav" == "q" ] && break; [ "$nav" == "0" ] && cd .. && continue; sel="${dirs[nav-1]}"; [ -d "$sel" ] && cd "$sel"; done; main_menu; }
change_permissions(){ clear; files=(*); echo "Select a file:"; for i in "${!files[@]}"; do echo "$((i+1)). ${files[i]}"; done; read -p "Enter file number: " i; file="${files[i-1]}"; [ ! -e "$file" ] && sleep 1 && change_permissions; clear; echo "Select permission:"; opts=("1.  755 (rwxr-xr-x) → Owner: all rights; Group & Others: read + execute" "2.  644 (rw-r--r--) → Owner: read + write; Group & Others: read only" "3.  700 (rwx------) → Owner: all rights; Group & Others: no access" "4.  600 (rw-------) → Owner: read + write; Group & Others: no access" "5.  775 (rwxrwxr-x) → Owner & Group: all rights; Others: read + execute" "6.  666 (rw-rw-rw-) → Everyone: read + write; no execute rights" "7.  444 (r--r--r--) → Everyone: read-only access" "8.  777 (rwxrwxrwx) → Full access to everyone (not secure)" "9.  Default (ugo+rw) → Add read + write for all users" "10. 711 (rwx--x--x) → Owner: all rights; Others: execute only" "11. 640 (rw-r-----) → Owner: read + write; Group: read; Others: none" "12. 755 -R → Recursive owner full; Group & Others: read + execute" "13. 000 (----------) → No access for anyone" "14. 555 (r-xr-xr-x) → Read + execute for all; no write" "15. 700 -R → Recursive owner only full access" "16. 711 -R → Recursive owner full; Others: execute only" "17. 664 (rw-rw-r--) → Owner & Group: read + write; Others: read" "18. 600 -R → Recursive owner read + write; others denied" "19. 444 -R → Recursive read-only for all" "20. 777 -R → Recursive full access for all (very unsafe)"); for o in "${opts[@]}"; do echo "$o"; done; read -p "Enter permission number: " p; case $p in 1) chmod 755 "$file" ;; 2) chmod 644 "$file" ;; 3) chmod 700 "$file" ;; 4) chmod 600 "$file" ;; 5) chmod 775 "$file" ;; 6) chmod 666 "$file" ;; 7) chmod 444 "$file" ;; 8) chmod 777 "$file" ;; 9) chmod ugo+rw "$file" ;; 10) chmod 711 "$file" ;; 11) chmod 640 "$file" ;; 12) chmod -R 755 "$file" ;; 13) chmod 000 "$file" ;; 14) chmod 555 "$file" ;; 15) chmod -R 700 "$file" ;; 16) chmod -R 711 "$file" ;; 17) chmod 664 "$file" ;; 18) chmod -R 600 "$file" ;; 19) chmod -R 444 "$file" ;; 20) chmod -R 777 "$file" ;; *) sleep 1 && change_permissions ;; esac; sleep 1 && main_menu; }
reset(){ clear; sleep 1; main_menu; }
main_menu
