#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';W(){ echo "$1"|fold -s -w 78;};B(){ L=80;printf "${E}+";printf -- '-%.0s' $(seq 1 $L);printf "+${R}\n";for l in "$@";do W "$l"|while read -r w;do printf "${E}|${R} %-78s ${E}|${R}\n" "$w";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 $L);printf "+${R}\n";};D(){ clear;B "9 of Hearts: (Mn: Manganese, Fe: Iron, Co: Cobalt)" "These elements are crucial for building spacecraft, life-support systems, and energy storage." "Manganese is used in alloys, iron in structural components, and cobalt in batteries.";echo;B "1. Manganese (Mn)" "2. Iron (Fe)" "3. Cobalt (Co)" "" "Input required: Select an element (1-3) or press 0 to exit.";};ELEM=( "Mn|Manganese|25|54.94|Manganese is a key component in steel and aluminum alloys, providing strength and resistance to wear. It is essential for spacecraft materials subjected to extreme stresses." "Fe|Iron|26|55.85|Iron is used in spacecraft structures and shielding. Its abundance and versatility make it a foundational element for building robust and durable components in space exploration." "Co|Cobalt|27|58.93|Cobalt is used in the production of high-capacity batteries and magnets for spacecraft. It is vital for energy storage and efficient power systems in long-duration missions." );while :;do D;echo -e "${E}";read -p "Enter your choice: " C;echo -e "${R}";[[ "$C" == 0 ]]&&{ clear;B "Thank you!" "Exiting the 9 of Hearts educational card.";break;};((C>=1&&C<=3))&&{ IFS='|' read -r S N Z A DESC<<<"${ELEM[C-1]}";clear;B "Element Details" "Symbol: $S" "Name: $N" "Atomic Number: $Z" "Atomic Weight: $A";echo;B "Description:" "$DESC";echo -e "${E}";read -n1 -s -r -p "Press any key to return to the main menu...";echo -e "${R}";}||{ B "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";};done
