#!/bin/bash
EMERALD='\x1b[38;5;85m';RESET='\033[0m';wrap_text(){ echo "$1"|fold -s -w 78; };draw_box(){ local content=("$@");local width=80;printf "${EMERALD}+";printf -- '-%.0s' $(seq 1 $width);printf "+${RESET}\n";for line in "${content[@]}";do wrap_text "$line"|while IFS= read -r l;do printf "${EMERALD}|${RESET} %-78s ${EMERALD}|${RESET}\n" "$l";done;done;printf "${EMERALD}+";printf -- '-%.0s' $(seq 1 $width);printf "+${RESET}\n";};display_card(){ clear;draw_box "3 of Hearts: (N: Nitrogen, O: Oxygen, F: Fluorine)" "These elements are essential for life support and chemical processes in space." "Nitrogen is used in spacecraft atmospheres, oxygen sustains life, and fluorine" "is key in fuel and material chemistry.";echo;draw_box "1. Nitrogen (N)" "2. Oxygen (O)" "3. Fluorine (F)" "" "Input required: Select an element (1-3) or press 0 to exit.";};display_element(){ local s="$1" nm="$2" z="$3" w="$4" d="$5";clear;draw_box "Element Details" "Symbol: $s" "Name: $nm" "Atomic Number: $z" "Atomic Weight: $w";echo;draw_box "Description:" "$d";};elements=("N|Nitrogen|7|14.01|Nitrogen makes up a significant portion of spacecraft atmospheres. It helps maintain pressure and is inert, reducing fire risks in enclosed spaces during missions." "O|Oxygen|8|16.00|Oxygen is essential for breathing in manned space missions. It is supplied in spacecraft life-support systems and stored as a liquid in cryogenic tanks for efficiency." "F|Fluorine|9|18.998|Fluorine is used in chemical propellants and the manufacturing of resilient materials like Teflon for spacecraft. Its reactivity makes it valuable in various space-related chemical processes.");while true;do display_card;echo -e "${EMERALD}";read -p "Enter your choice: " c;echo -e "${RESET}";if [[ "$c" == "0" ]];then clear;draw_box "Thank you!" "Exiting the 3 of Hearts educational card.";break;elif (( c>=1 && c<=3 ));then IFS='|' read -r s nm z w d <<<"${elements[$((c-1))]}";display_element "$s" "$nm" "$z" "$w" "$d";echo -e "${EMERALD}";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "${RESET}";else draw_box "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";sleep 2;fi;done
