#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';wrap(){ echo "$1"|fold -s -w 78; };box(){ local a=("$@");w=80;printf "${E}+%s+${R}\n" "$(printf -- '-%.0s' $(seq 1 $w))";for l in "${a[@]}";do wrap "$l"|while read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+%s+${R}\n" "$(printf -- '-%.0s' $(seq 1 $w))";};card(){ clear;box "7 of Hearts: (K: Potassium, Ca: Calcium, Sc: Scandium)" "These elements are important for biological functions, structural materials, and specialized alloys in space." "Potassium regulates astronaut health, calcium supports bone maintenance, and scandium strengthens spacecraft materials.";echo;box "1. Potassium (K)" "2. Calcium (Ca)" "3. Scandium (Sc)" "" "Input required: Select an element (1-3) or press 0 to exit.";};element(){ local s="$1" n="$2" z="$3" w="$4" d="$5";clear;box "Element Details" "Symbol: $s" "Name: $n" "Atomic Number: $z" "Atomic Weight: $w";echo;box "Description:" "$d";};elements=("K|Potassium|19|39.10|Potassium is vital for maintaining astronaut health by balancing cellular functions and nerve signaling. It is included in space food and supplements to prevent muscle cramping and fatigue." "Ca|Calcium|20|40.08|Calcium is essential for bone maintenance in microgravity. Astronauts receive calcium supplements to counteract bone density loss caused by prolonged time in space." "Sc|Scandium|21|44.96|Scandium is used in lightweight, high-strength alloys for spacecraft. It enhances structural integrity while keeping the overall mass low, making it ideal for aerospace applications.");while true;do card;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";if [[ "$c" == "0" ]];then clear;box "Thank you!" "Exiting the 7 of Hearts educational card.";break;elif (( c>=1 && c<=3 ));then IFS='|' read -r s n z w d <<<"${elements[$((c-1))]}";element "$s" "$n" "$z" "$w" "$d";echo -e "${E}";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "${R}";else box "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";sleep 2;fi;done
