#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "2 of Spades: Pr Praseodymium, Nd Neodymium and Pm Promethium" "These lanthanoids are vital for optics, magnets and energy systems in space technologies." "Praseodymium enhances optical systems, neodymium powers strong magnets, and promethium fuels long-lasting batteries.";echo;b "1. Praseodymium (Pr)" "2. Neodymium (Nd)" "3. Promethium (Pm)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pr|Praseodymium|59|140.91|Praseodymium is used in optical glasses and fiber optics to enhance lasers and space optics." "Nd|Neodymium|60|144.24|Neodymium is essential for powerful magnets in spacecraft motors and precision mechanisms." "Pm|Promethium|61|145|Promethium powers long-life nuclear batteries and luminous tools for deep-space systems.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 2 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
