#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "8 of Diamonds: (Pm: Promethium, Sm: Samarium, Eu: Europium)" "These rare earth elements are important for nuclear batteries, magnets, and phosphors in display technologies." "Promethium is used in nuclear batteries, samarium in magnets, and europium in red and blue phosphors for screens.";echo;b "1. Promethium (Pm)" "2. Samarium (Sm)" "3. Europium (Eu)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pm|Promethium|61|145|Promethium is used in nuclear batteries for deep-space probes and long missions where solar energy is limited." "Sm|Samarium|62|150.36|Samarium is found in high-strength magnets powering spacecraft motors and miniaturized actuators." "Eu|Europium|63|151.96|Europium enhances screen color in display systems, vital for control panels and imaging tools.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 8 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
