#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "5 of Clubs: Pa Protactinium, U Uranium and Np Neptunium" "These actinides support nuclear science and space energy systems. Protactinium advances nuclear research, Uranium fuels reactors, and Neptunium enables long-term spacecraft power.";echo;b "1. Protactinium (Pa)" "2. Uranium (U)" "3. Neptunium (Np)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pa|Protactinium|91|231.04|Protactinium is used in nuclear research for understanding reactor behavior and atomic structure." "U|Uranium|92|238.03|Uranium fuels nuclear power and enables long-lasting energy for deep-space systems." "Np|Neptunium|93|237|Neptunium is being studied for spacecraft power via radioisotope generators.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 5 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
