#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Jack of Spades: Rn Radon, Fr Francium and Ra Radium" "These radioactive elements have niche roles in science and medicine. Radon supports therapy, francium drives atomic research, and radium once illuminated tools and treated cancer.";echo;b "1. Radon (Rn)" "2. Francium (Fr)" "3. Radium (Ra)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Rn|Radon|86|222|Radon is a radioactive noble gas used in radiation therapy for cancer treatment. It is also studied for its geological and environmental significance." "Fr|Francium|87|223|Francium is a highly radioactive alkali metal. While it has no significant commercial applications, it is valuable for atomic research and fundamental physics experiments." "Ra|Radium|88|226|Radium was historically used in luminescent paints and clocks. Its radioactive properties also made it an early tool for cancer treatment in medicine.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Jack of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
