#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "King of Hearts: (Rb: Rubidium, Sr: Strontium, Y: Yttrium)" "These elements are important for timekeeping, materials science, and superconductors in space." "Rubidium is used in atomic clocks, strontium in advanced ceramics, and yttrium in high-temperature superconductors.";echo;b "1. Rubidium (Rb)" "2. Strontium (Sr)" "3. Yttrium (Y)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Rb|Rubidium|37|85.47|Rubidium is used in atomic clocks, which provide precise timekeeping necessary for satellite navigation and synchronization of systems in space missions." "Sr|Strontium|38|87.62|Strontium is a key component in advanced ceramics used in spacecraft for their thermal stability and resistance to extreme temperature variations." "Y|Yttrium|39|88.91|Yttrium is used in high-temperature superconductors, which enable efficient energy transmission and powerful magnetic systems in space applications.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the King of Hearts educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
