#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@"); printf "${E}+"; printf -- '-%.0s' {1..80}; printf "+${R}\n"; for l in "${a[@]}"; do w "$l"|while read -r x; do printf "${E}|${R} %-78s ${E}|${R}\n" "$x"; done; done; printf "${E}+"; printf -- '-%.0s' {1..80}; printf "+${R}\n"; };d(){ clear;b "Queen of Hearts: (Se: Selenium, Br: Bromine, Kr: Krypton)" "These elements are essential for imaging technologies, sterilization, and lighting systems in space." "Selenium is used in photocells, bromine in water purification, and krypton in high-efficiency lighting.";echo;b "1. Selenium (Se)" "2. Bromine (Br)" "3. Krypton (Kr)" "" "Input required: Select an element (1-3) or press 0 to exit."; };s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5"; };e=("Se|Selenium|34|78.96|Selenium is used in photocells and solar panels for its ability to convert light into electricity. It is crucial for imaging devices and power systems in space." "Br|Bromine|35|79.90|Bromine is vital for water purification systems in spacecraft. It ensures astronauts have access to clean drinking water by eliminating harmful microorganisms." "Kr|Krypton|36|83.80|Krypton is used in high-efficiency lighting systems and some ion propulsion technologies. Its inert nature and brightness make it useful for spacecraft illumination."); while :; do d; echo -e "${E}"; read -p "Enter your choice: " c; echo -e "${R}"; [[ "$c" == 0 ]] && clear && b "Thank you!" "Exiting the Queen of Hearts educational card." && break; (( c>=1 && c<=3 )) && IFS='|' read -r y n a w m <<<"${e[$((c-1))]}" && s "$y" "$n" "$a" "$w" "$m" && echo -e "${E}" && read -n1 -s -r -p "Press any key to return to the main menu..." && echo -e "${R}" || b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit."; done
