#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "5 of Diamonds: (Te: Tellurium, I: Iodine, Xe: Xenon)" "These elements are essential for thermoelectric devices, medical applications, and ion propulsion systems." "Tellurium is used in thermoelectrics, iodine in medical diagnostics, and xenon in spacecraft propulsion.";echo;b "1. Tellurium (Te)" "2. Iodine (I)" "3. Xenon (Xe)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Te|Tellurium|52|127.60|Tellurium is used in thermoelectric devices that convert heat into electricity. It is vital for energy systems in spacecraft where efficiency is crucial." "I|Iodine|53|126.90|Iodine is used in medical diagnostics and water purification systems aboard spacecraft. Its applications ensure astronaut health and safety during missions." "Xe|Xenon|54|131.29|Xenon is a key element in ion propulsion systems, providing efficient and reliable thrust for long-duration space missions. It is widely used in modern spacecraft engines.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 5 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
