#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Ace of Diamonds: (Zr: Zirconium, Nb: Niobium, Mo: Molybdenum)" "These elements are vital for high-performance materials and heat-resistant alloys." "Zirconium is used in nuclear reactors, niobium in superconductors, and molybdenum in aerospace alloys.";echo;b "1. Zirconium (Zr)" "2. Niobium (Nb)" "3. Molybdenum (Mo)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Zr|Zirconium|40|91.22|Zirconium is used in cladding for nuclear reactors and components that must endure high temperatures and resist corrosion. It plays a key role in space nuclear propulsion systems." "Nb|Niobium|41|92.91|Niobium is critical for superconducting materials, enabling high-efficiency energy transfer and magnetic systems in spacecraft and satellite technologies." "Mo|Molybdenum|42|95.95|Molybdenum is used in aerospace alloys for its strength and resistance to high temperatures. It is ideal for components exposed to extreme heat, such as engine parts.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Ace of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m <<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
